 canvasRect = Rect(
 b.left.roundToInt(),
 b.top.roundToInt(),
 b.right.roundToInt(),
 b.bottom.roundToInt()
 )
 }
 ) {
 WallpaperCanvas(
 painter = painterResource(id = wallpaper.resourceId),
 glassStyle = glassStyle
 )
 }

